mapper.select:
SELECT product.product_no, category.category_name, supplier.supplier_name, product.product_name, COALESCE(purchase_summary.total_purchase_quantity, 0) - COALESCE(order_summary.total_order_quantity, 0) AS inventory_quantity, product.safety_quantity, product.product_price, product.product_img, product.product_remark FROM product LEFT JOIN category ON product.category_no = category.category_no LEFT JOIN supplier ON product.supplier_no = supplier.supplier_no -- 서브쿼리로 중복 제거한 구매 수량 LEFT JOIN ( SELECT product_no, SUM(purchase_quantity) AS total_purchase_quantity FROM purchase GROUP BY product_no ) AS purchase_summary ON product.product_no = purchase_summary.product_no -- 서브쿼리로 중복 제거한 주문 수량 LEFT JOIN ( SELECT product_no, SUM(product_quantity) AS total_order_quantity FROM order_item GROUP BY product_no ) AS order_summary ON product.product_no = order_summary.product_no WHERE 1 = 1
and product.category_no = #{categoryNo}
and product.supplier_no = #{supplierNo}
and product.product_name like concat('%', #{productName}, '%')
and product.product_price >= cast(#{startProductPrice} as integer)
and product.product_price <= cast(#{endProductPrice} as integer)
and product.product_price between cast(#{startProductPrice} as integer) and cast(#{endProductPrice} as integer)
and product.product_remark = #{productRemark}
GROUP BY product.product_no, category.category_name, supplier.supplier_name, product.product_name, product.safety_quantity, product.product_price, product.product_img, product.product_remark, purchase_summary.total_purchase_quantity, -- 추가된 부분 order_summary.total_order_quantity -- 추가된 부분 ORDER BY product.product_no DESC LIMIT #{amount} OFFSET #{pageStart}

select count(*)
from product
where 1 = 1

    and product.category_no = #{categoryNo}


    and product.supplier_no = #{supplierNo}


    and product.product_name like concat('%', #{productName}, '%')


     and product.product_price >= cast(#{startProductPrice} as integer) 


     and product.product_price <= cast(#{endProductPrice} as integer) 


     and product.product_price between cast(#{startProductPrice} as integer) and cast(#{endProductPrice} as integer) 


     and product.product_remark = #{productRemark}

select product_no, product.category_no, category_name, product.supplier_no, supplier_name, supplier_business_no, manager_name, manager_phone, product_name, product_content, safety_quantity, product_price, product_img, product_remark
from product left join category on product.category_no = category.category_no left join supplier on product.supplier_no = supplier.supplier_no
where product_no = #{productNo}
